"use client";

import { useState } from "react";
import { authClient } from "@/lib/auth-client";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  MoreHorizontal,
  Shield,
  Ban,
  Unlock,
  Trash2,
  User,
} from "lucide-react";
import { toast } from "sonner";

interface User {
  id: string;
  name: string | null;
  email: string;
  role: string;
  banned: boolean;
  banReason?: string | null;
  banExpires?: Date | null;
  createdAt?: Date | null;
}

interface UserActionsProps {
  user: User;
  onUpdate: () => void;
}

export function UserActions({ user, onUpdate }: UserActionsProps) {
  const [roleDialogOpen, setRoleDialogOpen] = useState(false);
  const [banDialogOpen, setBanDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState(user.role || "user");
  const [banReason, setBanReason] = useState("");
  const [loading, setLoading] = useState(false);

  const handleRoleChange = async () => {
    setLoading(true);
    try {
      // Type assertion needed because Better Auth types don't include custom adminRoles
      await authClient.admin.setRole({
        userId: user.id,
        role: selectedRole as "user" | "admin",
      });
      toast.success(`Role updated to ${selectedRole}`);
      setRoleDialogOpen(false);
      onUpdate();
    } catch (error) {
      toast.error("Failed to update role");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleBanUser = async () => {
    setLoading(true);
    try {
      await authClient.admin.banUser({
        userId: user.id,
        banReason: banReason || "Violated terms of service",
      });
      toast.success(`User ${user.name || user.email} has been banned`);
      setBanDialogOpen(false);
      onUpdate();
    } catch (error) {
      toast.error("Failed to ban user");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleUnbanUser = async () => {
    setLoading(true);
    try {
      await authClient.admin.unbanUser({
        userId: user.id,
      });
      toast.success(`User ${user.name || user.email} has been unbanned`);
      onUpdate();
    } catch (error) {
      toast.error("Failed to unban user");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async () => {
    setLoading(true);
    try {
      await authClient.admin.removeUser({
        userId: user.id,
      });
      toast.success(`User ${user.name || user.email} has been deleted`);
      setDeleteDialogOpen(false);
      onUpdate();
    } catch (error) {
      toast.error("Failed to delete user");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="ghost" size="sm">
            <MoreHorizontal className="h-4 w-4" />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end">
          <DropdownMenuLabel>Actions</DropdownMenuLabel>
          <DropdownMenuSeparator />
          <DropdownMenuItem onClick={() => setRoleDialogOpen(true)}>
            <Shield className="mr-2 h-4 w-4" />
            Change Role
          </DropdownMenuItem>
          {user.banned ? (
            <DropdownMenuItem onClick={handleUnbanUser} disabled={loading}>
              <Unlock className="mr-2 h-4 w-4" />
              Unban User
            </DropdownMenuItem>
          ) : (
            <DropdownMenuItem onClick={() => setBanDialogOpen(true)}>
              <Ban className="mr-2 h-4 w-4" />
              Ban User
            </DropdownMenuItem>
          )}
          <DropdownMenuSeparator />
          <DropdownMenuItem
            onClick={() => setDeleteDialogOpen(true)}
            className="text-destructive focus:text-destructive"
          >
            <Trash2 className="mr-2 h-4 w-4" />
            Delete User
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>

      {/* Role Change Dialog */}
      <Dialog open={roleDialogOpen} onOpenChange={setRoleDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Change User Role</DialogTitle>
            <DialogDescription>
              Update the role for {user.name || user.email}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="role">Role</Label>
              <Select value={selectedRole} onValueChange={setSelectedRole}>
                <SelectTrigger id="role">
                  <SelectValue placeholder="Select a role" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="user">User</SelectItem>
                  <SelectItem value="admin">Admin</SelectItem>
                  <SelectItem value="super-admin">Super Admin</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setRoleDialogOpen(false)}
              disabled={loading}
            >
              Cancel
            </Button>
            <Button onClick={handleRoleChange} disabled={loading}>
              {loading ? "Updating..." : "Update Role"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Ban User Dialog */}
      <Dialog open={banDialogOpen} onOpenChange={setBanDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Ban User</DialogTitle>
            <DialogDescription>
              This will prevent {user.name || user.email} from accessing the platform
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="reason">Reason (optional)</Label>
              <Textarea
                id="reason"
                placeholder="Enter reason for ban..."
                value={banReason}
                onChange={(e) => setBanReason(e.target.value)}
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setBanDialogOpen(false)}
              disabled={loading}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleBanUser}
              disabled={loading}
            >
              {loading ? "Banning..." : "Ban User"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete User Dialog */}
      <Dialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Delete User</DialogTitle>
            <DialogDescription>
              Are you sure you want to permanently delete {user.name || user.email}? This
              action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setDeleteDialogOpen(false)}
              disabled={loading}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleDeleteUser}
              disabled={loading}
            >
              {loading ? "Deleting..." : "Delete User"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { client } from "@/lib/orpc";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Spinner } from "@/components/ui/spinner";
import { UserActions } from "./user-actions";
import { Users, RefreshCw } from "lucide-react";
import { toast } from "sonner";

interface User {
  id: string;
  name: string | null;
  email: string;
  role: string;
  banned: boolean;
  banReason?: string | null;
  banExpires?: Date | null;
  createdAt?: Date | null;
}

export function UserManagement() {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      // Fetch users via oRPC
      const data = await client.admin.getUsers();
      setUsers(data.users);
    } catch (error) {
      console.error("Failed to fetch users:", error);
      toast.error(
        error instanceof Error ? error.message : "Failed to fetch users"
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleUserUpdate = () => {
    fetchUsers();
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Users className="h-5 w-5 text-amber-500" />
            <div>
              <CardTitle>User Management</CardTitle>
              <CardDescription>
                Manage users, roles, and permissions
              </CardDescription>
            </div>
          </div>
          <Button
            onClick={fetchUsers}
            variant="outline"
            size="sm"
            disabled={loading}
          >
            <RefreshCw
              className={`mr-2 h-4 w-4 ${loading ? "animate-spin" : ""}`}
            />
            Refresh
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <Spinner className="h-8 w-8" />
          </div>
        ) : users.length === 0 ? (
          <div className="text-center py-12 text-muted-foreground">
            No users found. Users will appear here once they sign up.
          </div>
        ) : (
          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Role</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Joined</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {users.map((user) => (
                  <TableRow key={user.id}>
                    <TableCell className="font-medium">
                      {user.name || "N/A"}
                    </TableCell>
                    <TableCell>{user.email}</TableCell>
                    <TableCell>
                      <Badge
                        variant={
                          user.role === "admin" || user.role === "super-admin"
                            ? "default"
                            : "secondary"
                        }
                      >
                        {user.role || "user"}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      {user.banned ? (
                        <Badge variant="destructive">Banned</Badge>
                      ) : (
                        <Badge
                          variant="outline"
                          className="text-green-500 border-green-500"
                        >
                          Active
                        </Badge>
                      )}
                    </TableCell>
                    <TableCell>
                      {user.createdAt
                        ? new Date(user.createdAt).toLocaleDateString()
                        : "N/A"}
                    </TableCell>
                    <TableCell className="text-right">
                      <UserActions user={user} onUpdate={handleUserUpdate} />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

"use client";

import { useRouter } from "next/navigation";
import { authClient } from "@/lib/auth-client";
import { Button } from "@/components/ui/button";
import { LogOut } from "lucide-react";
import { useState } from "react";
import { Spinner } from "@/components/ui/spinner";

export function SignOutButton() {
  const router = useRouter();
  const [loading, setLoading] = useState(false);

  const handleSignOut = async () => {
    setLoading(true);
    try {
      await authClient.signOut();
      router.push("/sign-in");
    } catch (error) {
      console.error("Failed to sign out:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Button onClick={handleSignOut} variant="outline" disabled={loading}>
      {loading ? (
        <>
          <Spinner className="mr-2 h-4 w-4" />
          Signing out...
        </>
      ) : (
        <>
          <LogOut className="mr-2 h-4 w-4" />
          Sign Out
        </>
      )}
    </Button>
  );
}

"use client";

import Link from "next/link";
import { useCallback, useEffect, useRef, useState } from "react";
import { toast } from "sonner";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

type GameState =
  | "waiting"
  | "ready"
  | "countdown"
  | "fire"
  | "result"
  | "staking"
  | "contract_ready";
type Winner = "player" | "ai" | "none";

type GameStats = {
  playerWins: number;
  aiWins: number;
};

type ContractState = {
  isConnected: boolean;
  isStaked: boolean;
  stakeAmount: bigint;
  gamePool: bigint;
  humanStake: bigint;
  botCount: bigint;
  gameStatus: bigint;
};

export default function QuickDrawGame() {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const activeAddress = process.env.NEXT_PUBLIC_WALLET_ADDRESS || null;

  const [gameState, setGameState] = useState<GameState>("waiting");
  const [winner, setWinner] = useState<Winner>("none");
  const [message, setMessage] = useState("Connect wallet to start staking!");
  const [stats, setStats] = useState<GameStats>({ playerWins: 0, aiWins: 0 });
  const [playerShot, setPlayerShot] = useState(false);
  const [aiShot, setAiShot] = useState(false);
  const [playerFell, setPlayerFell] = useState(false);
  const [aiFell, setAiFell] = useState(false);
  const [showInstructions, setShowInstructions] = useState(true);
  const [showResultDialog, setShowResultDialog] = useState(false);
  const [showReadyIndicator, setShowReadyIndicator] = useState(false);
  const [showStakeDialog, setShowStakeDialog] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showPostClaimDialog, setShowPostClaimDialog] = useState(false);

  const [contractState, setContractState] = useState<ContractState>({
    isConnected: false,
    isStaked: false,
    stakeAmount: BigInt(0),
    gamePool: BigInt(0),
    humanStake: BigInt(0),
    botCount: BigInt(0),
    gameStatus: BigInt(0),
  });

  const gameTimerRef = useRef<NodeJS.Timeout | undefined>(undefined);
  const aiReactionRef = useRef<NodeJS.Timeout | undefined>(undefined);
  const animationRef = useRef<number | undefined>(undefined);

  // Contract constants
  const STAKE_AMOUNT = BigInt(2_000_000); // 2 $
  const BOT_COUNT = BigInt(1); // 1 AI bot (minimum required by contract)
  const REWARD = Number(BOT_COUNT * STAKE_AMOUNT + STAKE_AMOUNT) / 1_000_000; // Reward is stake * number of bots

  const endGameWithResult = (humanWon: boolean) => {
    setIsLoading(true);
    try {
      if (humanWon) {
        toast.success(`You won ${REWARD} GEM! `, {
          description:
            "Congratulations! Your winnings have been sent to your wallet.",
        });
      } else {
        toast.info("Better luck next time!", {
          description: "Your stake has been returned to the pool.",
        });
      }
    } catch {
      toast.error("Failed to process game result. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const resetContractGame = () => {
    setIsLoading(true);
    try {
      toast.success("Game reset! Ready for next round.");
    } catch {
      toast.error("Failed to reset game. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };
  // Drawing functions
  const drawCactus = (
    ctx: CanvasRenderingContext2D,
    x: number,
    y: number,
    width: number,
    height: number
  ) => {
    ctx.fillStyle = "#16a34a";
    ctx.fillRect(x, y, width, height);
    // Arms
    ctx.fillRect(x - 15, y + 20, 15, 30);
    ctx.fillRect(x + width, y + 30, 15, 25);
  };

  const drawCowboy = (
    ctx: CanvasRenderingContext2D,
    x: number,
    y: number,
    facing: "left" | "right",
    shot: boolean,
    fell: boolean
  ) => {
    ctx.save();
    ctx.translate(x, y);
    if (facing === "right") {
      ctx.scale(-1, 1);
    }

    // Hat
    ctx.fillStyle = "#92400e";
    ctx.fillRect(-15, -40, 30, 8);
    ctx.fillRect(-20, -48, 40, 8);

    // Head
    ctx.fillStyle = "#fbbf24";
    ctx.fillRect(-10, -32, 20, 20);

    // Body
    ctx.fillStyle = "#1f2937";
    if (fell) {
      // Lying down
      ctx.fillRect(-30, -5, 40, 15);
      ctx.fillStyle = "#92400e"; // Arms
      ctx.fillRect(-35, -10, 15, 8);
      ctx.fillRect(20, -10, 15, 8);
    } else {
      // Standing
      ctx.fillRect(-12, -12, 24, 30);

      // Arms
      ctx.fillStyle = "#92400e";
      if (shot) {
        // Gun drawn
        ctx.fillRect(12, -8, 20, 6);
        ctx.fillStyle = "#374151"; // Gun
        ctx.fillRect(32, -6, 8, 3);
      } else {
        // Arms at sides
        ctx.fillRect(-18, -8, 8, 20);
        ctx.fillRect(10, -8, 8, 20);
      }

      // Legs
      ctx.fillStyle = "#1e40af";
      ctx.fillRect(-10, 18, 8, 20);
      ctx.fillRect(2, 18, 8, 20);
    }

    ctx.restore();
  };

  const drawMuzzleFlash = (
    ctx: CanvasRenderingContext2D,
    x: number,
    y: number
  ) => {
    ctx.fillStyle = "#fbbf24";
    ctx.beginPath();
    ctx.arc(x, y, 8, 0, Math.PI * 2);
    ctx.fill();

    ctx.fillStyle = "#f59e0b";
    ctx.beginPath();
    ctx.arc(x, y, 5, 0, Math.PI * 2);
    ctx.fill();
  };

  const drawReadyIndicator = (
    ctx: CanvasRenderingContext2D,
    x: number,
    y: number
  ) => {
    // Blinking "READY" text
    const time = Date.now() * 0.005;
    const alpha = (Math.sin(time) + 1) * 0.5;

    ctx.save();
    ctx.globalAlpha = alpha;
    ctx.fillStyle = "#fbbf24";
    ctx.font = "bold 48px Arial";
    ctx.textAlign = "center";
    ctx.fillText("READY", x, y);
    ctx.restore();
  };

  const drawFireText = (
    ctx: CanvasRenderingContext2D,
    x: number,
    y: number
  ) => {
    // Large "FIRE!" text
    ctx.save();
    ctx.fillStyle = "#ef4444";
    ctx.font = "bold 72px Arial";
    ctx.textAlign = "center";
    ctx.fillText("FIRE!", x, y);
    ctx.restore();
  };

  // Draw the game scene
  const drawScene = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) {
      return;
    }

    const ctx = canvas.getContext("2d");
    if (!ctx) {
      return;
    }

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Desert background gradient
    const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
    gradient.addColorStop(0, "#fbbf24"); // Golden sky
    gradient.addColorStop(0.6, "#f59e0b"); // Orange horizon
    gradient.addColorStop(1, "#d97706"); // Desert ground
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Sun
    ctx.fillStyle = "#fef3c7";
    ctx.beginPath();
    ctx.arc(canvas.width - 100, 80, 40, 0, Math.PI * 2);
    ctx.fill();

    // Ground
    ctx.fillStyle = "#92400e";
    ctx.fillRect(0, canvas.height - 100, canvas.width, 100);

    // Cacti
    drawCactus(ctx, 100, canvas.height - 150, 30, 80);
    drawCactus(ctx, canvas.width - 150, canvas.height - 130, 25, 70);

    // Player cowboy (left side)
    const playerY = playerFell ? canvas.height - 50 : canvas.height - 120;
    drawCowboy(ctx, 150, playerY, "left", playerShot, playerFell);

    // AI cowboy (right side)
    const aiY = aiFell ? canvas.height - 50 : canvas.height - 120;
    drawCowboy(ctx, canvas.width - 150, aiY, "right", aiShot, aiFell);

    // Muzzle flashes
    if (playerShot && !playerFell) {
      drawMuzzleFlash(ctx, 180, canvas.height - 100);
    }
    if (aiShot && !aiFell) {
      drawMuzzleFlash(ctx, canvas.width - 180, canvas.height - 100);
    }

    // Ready indicator (blinking "READY")
    if (showReadyIndicator && gameState === "ready") {
      drawReadyIndicator(ctx, canvas.width / 2, canvas.height / 2 - 50);
    }

    // Fire text (large "FIRE!")
    if (gameState === "fire") {
      drawFireText(ctx, canvas.width / 2, canvas.height / 2 - 50);
    }
  }, [playerShot, aiShot, playerFell, aiFell, showReadyIndicator, gameState]);

  // Game logic
  const endGame = useCallback((gameWinner: Winner, customMessage?: string) => {
    setGameState("result");
    setWinner(gameWinner);

    if (customMessage) {
      setMessage(customMessage);
    } else if (gameWinner === "player") {
      setMessage("You won the duel!");
      setAiFell(true);
      setStats((prev) => ({ ...prev, playerWins: prev.playerWins + 1 }));
    } else if (gameWinner === "ai") {
      setMessage("The AI outgunned you!");
      setPlayerFell(true);
      setStats((prev) => ({ ...prev, aiWins: prev.aiWins + 1 }));
    }

    // Show result dialog after a short delay
    setTimeout(() => {
      setShowResultDialog(true);
    }, 1000);

    // Clear timers
    if (gameTimerRef.current) {
      clearTimeout(gameTimerRef.current);
    }
    if (aiReactionRef.current) {
      clearTimeout(aiReactionRef.current);
    }
  }, []);

  const playerShoot = useCallback(() => {
    if (gameState === "fire" && !playerShot) {
      setPlayerShot(true);
      if (!aiShot) {
        endGame("player");
      }
    } else if (gameState === "ready" || gameState === "countdown") {
      // Shot too early - player loses
      setPlayerShot(true);
      endGame("ai", "You shot too early!");
    }
  }, [gameState, playerShot, aiShot, endGame]);

  const resetGame = useCallback(() => {
    setGameState("staking"); // Set to staking so user can stake again
    setMessage("Stake 2 GEM to play against 2 AI bots!");
    setWinner("none");
    setPlayerShot(false);
    setAiShot(false);
    setPlayerFell(false);
    setAiFell(false);
    setShowReadyIndicator(false);
    setShowResultDialog(false);
    setShowInstructions(false); // Don't show instructions again

    if (gameTimerRef.current) {
      clearTimeout(gameTimerRef.current);
    }
    if (aiReactionRef.current) {
      clearTimeout(aiReactionRef.current);
    }
  }, []);

  const claimReward = () => {
    if (winner === "player") {
      endGameWithResult(true);
    } else {
      // If AI won, just end the game (money stays in pool)
      endGameWithResult(false);
    }

    // Reset the contract game state so user can play again
    resetContractGame();

    // Reset the UI to allow staking again
    resetGame();
    setShowResultDialog(false);

    // Show post-claim dialog with options to stake again or go home
    setShowPostClaimDialog(true);
  };

  // Keyboard controls
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.code === "KeyA") {
        e.preventDefault();
        playerShoot();
      }
    };

    window.addEventListener("keydown", handleKeyPress);
    return () => window.removeEventListener("keydown", handleKeyPress);
  }, [playerShoot]);

  // Animation loop
  useEffect(() => {
    const animate = () => {
      drawScene();
      animationRef.current = requestAnimationFrame(animate);
    };
    animate();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [drawScene]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (gameTimerRef.current) {
        clearTimeout(gameTimerRef.current);
      }
      if (aiReactionRef.current) {
        clearTimeout(aiReactionRef.current);
      }
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  const stakeForGame = () => {
    setIsLoading(true);
    try {
      // Simulate successful staking
      setTimeout(() => {
        setContractState((prev) => ({
          ...prev,
          isConnected: true,
          isStaked: true,
          stakeAmount: STAKE_AMOUNT,
          botCount: BOT_COUNT,
          gameStatus: BigInt(1), // Game ready
        }));
        setGameState("ready");
        setMessage("Get ready...");

        // Show "READY" indicator for 2 seconds
        setShowReadyIndicator(true);
        gameTimerRef.current = setTimeout(() => {
          setShowReadyIndicator(false);
          setGameState("countdown");
          setMessage("Wait for it...");

          // After random delay, switch to "FIRE!" state

          const delay = Math.random() * 2000 + 1000; // 1-3 seconds
          gameTimerRef.current = setTimeout(() => {
            setGameState("fire");
            setMessage("FIRE!");

            // AI reaction time
            const aiReactionTime = Math.random() * 800 + 200; // 200-1000 ms
            aiReactionRef.current = setTimeout(() => {
              if (!playerShot) {
                setAiShot(true);
                endGame("ai");
              }
            }, aiReactionTime);
          }, delay);
        }, 2000);
        setShowStakeDialog(false);
        setIsLoading(false);
      }, 1000);
    } catch {
      toast.error("Staking failed. Please try again.");
      setIsLoading(false);
    }
  };

  return (
    <>
      {/* Instruction Dialog */}
      <Dialog open={showInstructions}>
        <DialogContent
          className="max-w-md"
          onEscapeKeyDown={(e) => e.preventDefault()}
          onInteractOutside={(e) => e.preventDefault()}
          onPointerDownOutside={(e) => e.preventDefault()}
        >
          <DialogHeader>
            <DialogTitle className="text-center font-mono text-2xl">
               QUICK DRAW SHOWDOWN 
            </DialogTitle>
            <DialogDescription className="space-y-4 text-center">
              <span className="block font-semibold text-lg">How to Play:</span>
              <span className="block">
                 Stake 2 GEM to play against 1 AI bot
              </span>
              <span className="block"> Click START to begin the duel</span>
              <span className="block">
                 Wait for the &quot;FIRE!&quot; command
              </span>
              <span className="block rounded-lg bg-yellow-100 p-4 font-bold text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
                 Stake: 2 GEM | Prize: 4 GEM | Bots: 1
              </span>
              <Button
                className="w-full font-mono"
                onClick={() => {
                  setShowInstructions(false);
                  setShowStakeDialog(true);
                }}
                size="lg"
              >
                {"START DUEL"}
              </Button>
              <Link className="w-full" href="/games">
                <Button
                  className="w-full font-mono"
                  size="lg"
                  variant="outline"
                >
                   GO TO GAMES
                </Button>
              </Link>
            </DialogDescription>
          </DialogHeader>
        </DialogContent>
      </Dialog>

      {/* Staking Dialog */}
      <Dialog open={showStakeDialog}>
        <DialogContent
          className="max-w-md"
          onEscapeKeyDown={(e) => e.preventDefault()}
          onInteractOutside={(e) => e.preventDefault()}
          onPointerDownOutside={(e) => e.preventDefault()}
        >
          <DialogHeader>
            <DialogTitle className="text-center font-mono text-2xl">
               STAKE TO PLAY 
            </DialogTitle>
            <DialogDescription asChild>
              <div className="space-y-4 text-center">
                <div className="rounded-lg bg-blue-100 p-4 dark:bg-blue-900">
                  <p className="font-bold text-blue-800 dark:text-blue-200">
                    Game Details:
                  </p>
