                {activeAddress ? "Connected" : "Disconnected"}
              </span>
            </div>
            {contractState.gamePool > BigInt(0) && (
              <>
                <div className="h-4 w-px bg-border" />
                <span className="text-muted-foreground text-xs">
                  Pool: 80 $
                </span>
              </>
            )}
